import React from "react"

const MenuItem = ({title, ImageUrl}) => {
    return ( 
        <div style={{backgroundImage: `url(${ImageUrl})`}} className="border-black border-2 h-56 my-auto flex-auto" >
            <div className="my-auto flex flex-col items-center justify-center h-52 bg-white">
                <p className="block uppercase text-md" >
                    {title}
                </p>
                <span className="block uppercase">
                    Shop Now
                </span>
            </div>
        </div>
     );
}
 
export default MenuItem;
