import React from "react"
import { Route } from "react-router-dom";
import CollectionPage from "./collection-page/CollectionPage.jsx";
import { createStructuredSelector } from "reselect";
import { fetchCollectionsStartAsync } from "../react/shop/shop.action";
import { selectIsCollectionsLoaded } from "../react/shop/shop.selector.js";
import {connect} from "react-redux";
import WithSpinner from "../Components/Collections/with-spinner/with-spinner.jsx";
import CollectionOverviewContainer from "../Components/Collections/collection-overview.container.jsx";

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {


  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props
    fetchCollectionsStartAsync();
    
  }

  render() {
    const { match, IsCollectionsLoaded } = this.props;
    return (
      <div className='shop-page'>
        <Route
          exact
          path={`${match.path}`}
          component={CollectionOverviewContainer}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner isLoading={!IsCollectionsLoaded} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  IsCollectionsLoaded: selectIsCollectionsLoaded
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ShopPage);
