import cartTypes from "./cart.types"

export const toggleCartHidden = () => ({
    type: cartTypes.TOGGLE_CART_HIDDEN
})

export const addItems = item => ({
    type: cartTypes.ADD_ITEMS,
    payload: item
})

export default toggleCartHidden;
