import { takeLatest, put, all, call } from "@redux-saga/core/effects";

import userActionTypes from "./user.types";

import { auth, googleProvider, createUserProfileDocument } from "../../Components/firebase/firebase-utils";
import { googleSignInSuccess, googleSignInFailure, emailSignInSuccess, emailSignInFailure } from "./user.action";


export function* signInWithGoogle() {
   try{
    const {user} = yield auth.signInWithPopup(googleProvider)
    const userRef = yield call(createUserProfileDocument, user)
    const userSnapshot = yield userRef.get();
    yield put(
        googleSignInSuccess({id: userSnapshot.id, ...userSnapshot.data()})
    );
   } catch (error) {
    yield put(
        googleSignInFailure(error.message)
    );
   };
};

export function* signInWithEmail({payload: {email, password}}) {
    try {
        const {user} = yield auth.signInWithEmailAndPassword(email, password)
        const userRef = yield call(createUserProfileDocument, user)
        const userSnaphot = yield userRef.get()
        yield put(
            emailSignInSuccess({id: userSnaphot.id, ...userSnaphot.data()})
        );
    } catch (error) {
        yield put(
            emailSignInFailure(error.message)
        );
    };
};

export function* onGoogleSignInStart() {
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
};

export function* onEmailSignInStart() {
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, signInWithEmail)
};

export function* userSaga() {
    yield all([call(onGoogleSignInStart)])

};
