import React from "react"
import CustomButton from "../CustomButton/CustomButton"
import { connect } from "react-redux";
import { addItems } from "../../react/cart/cart.actions";
import "../Collections/collection-item.styles.scss"

const collectionItem = ({items, addItems }) => {

    const { imageUrl, name, price } = items;

    return ( 
        <div className='collection-item'>
      <div
        className='image'
        style={{
          backgroundImage: `url(${imageUrl})`
        }}
      />
      <div className='collection-footer'>
        <span className='name'>{name}</span>
        <span className='price'>{price}</span>
      </div>
      <CustomButton onClick={() => addItems(items)} inverted>
        Add to cart
      </CustomButton>
    </div>
     );
}

const mapDispatchToProps = dispatch => ({
    addItems: items => dispatch(addItems(items))
})
 
export default connect(null, mapDispatchToProps) (collectionItem);
