import cartTypes from "./cart.types";

const INITIAL_STATE = {
    hidden: true
}

const cartReducer = (state=INITIAL_STATE, action) => {
    switch (action.types) {
        case cartTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            }
        default:
            return state;
    }
}

export default cartReducer
